sources.scss/basket_section.scss:
#basket-section {
    padding: 50px 0px;
    border-radius: 30px;
    position: relative;
    background-color: rgba(128, 128, 128, 0.066);
    min-height: 100vh;
    z-index: 1;
    display: none;

    #basket-section-wrap {
        margin-top: 20px;
        width: 100%;
        display: flex;
        align-items: flex-start;
        justify-content: space-between;

        #basket-left-wrap {
            width: 65%;
            border-radius: 20px;
            padding: 40px;
            position: relative;
            background-color: white;

            &-title {
                font-size: 22px;
                margin: 0px 10px    ;
            }

            #basket-left-chevron {
                position: absolute;
                right: 5%;

                #chevron-up,
                #chevron-down {
                    border: none;
                    background-color: transparent;
                    cursor: pointer;
                    padding: 2px;
                    font-size: 22px;
                    color: gray;

                    .fa-chevron-up {
                        display: block;
                    }

                    #chevron-down,
                    .fa-chevron-down  {
                        display: none;
                    }

                    &:hover {
                        color: black;
                    }

                    .active-chevron {
                        display: none;
                    }
                }

            }

            #basket-left-cards-wrap {

                .basket-left-card {
                    display: flex;
                    justify-content: space-between  ;
                    margin-top: 35px;
                    position: relative;
                    height: 200px;

                    #basket-left-card-img {
                        width: 22%;
                        margin: 0px 10px;
                        height: 100%;

                        & img {
                            width: 100%;
                            border-radius: 20px;
                            cursor: pointer;
                            height: 100%;
                        }
                    }

                    #basket-left-card-product {
                        width: 35%;
                        margin: 10px 15px;
                        font-size: 18px;

                        #basket-company {
                            font-weight: 700;
                        }
                    }

                    #basket-left-card-counter {
                        margin: 10px 15px;

                        #basket-counter-minus,
                        #basket-counter-plus {
                            width: 30px;
                            height: 30px;
                            cursor: pointer;
                            border: none;
                            border-radius: 5px;
                            font-size: 16px;
                            background-color: rgba(128, 128, 128, 0.102);

                            &:hover {
                            background-color: rgba(128, 128, 128, 0.265);
                            }
                        }

                        #basket-counter-sum {
                            font-size: 20px;
                            font-weight: 400;
                            margin: 0px 10px;
                        }
                    }

                    #basket-left-card-price {
                        display: flex;
                        flex-direction: column;
                        align-items: end;
                        margin: 10px 15px; 

                        #basket-current-price {
                            font-weight: 700;
                            font-size: 22px;
                        }

                        #basket-price-before-discount {
                            font-weight: 400;
                            font-size: 16px;
                            text-decoration: line-through;
                            margin-top: 10px;
                            color: gray;
                        }

                        .basket-dollar {
                            margin-left: 5px;
                        }

                    }

                    #basket-left-card-icons {
                        position: absolute;
                        right: 0;
                        bottom: 35%;
                        display: none;
                    }

                    &:hover {
                        #basket-left-card-icons {
                            display: block;
                        }
                        .fa-trash,
                        .fa-heart {
                            padding: 5px;
                            margin: 0px 5px;
                            cursor: pointer;
                            font-size: 20px;
                            color: rgba(128, 128, 128, 0.501);

                            &:hover {
                                color: black;
                            }
                        }
                    }
                }
            }
        }

        #basket-right-wrap {
            width: 30%;
            border-radius: 20px;
            padding: 40px;
            position: relative;
            background-color: white;

            #basket-right-wrap-top {
                display: flex;
                justify-content: space-between;

                .goods,
                .goods-price-sum-top {
                    font-size: 18px;
                    color: gray;
                }

                .total,
                .goods-price-sum-bottom {
                    margin-top: 20px;
                    font-size: 24px;
                    font-weight: 700;
                }

                #basket-price-goods {
                    display: flex;
                    flex-direction: column;
                    align-items: end;
                }

                #goods-quantity {
                    font-size: 24px;
                    color: black;
                    font-weight: 400;
                }
            }

            #basket-right-btn-wrap {
                margin-top: 40px;

                #basket-right-btn {
                    width: 100%;
                    height: 60px;
                    cursor: pointer;
                    border-radius: 10px;
                    border: none;
                    color: white;
                    font-size: 20px;
                    background-color: rgb(217, 0, 255);

                    &:hover {
                        background-color:rgb(222, 33, 255);
                    }
                }
            }
        }
    }
}
