#header {
    padding: 40px 0px 60px;
    background: linear-gradient(to right,#cb11ab 0,#481173 100%);
    position: absolute;
    width: 100%;

    #header-wrap {
        display: flex;
        justify-content: space-between;
        align-items: center;

        #header-logo {
            color: white;
            font-size: 50px;
            text-transform: uppercase;
            margin: 0px 10px;
            max-width: 331px;
            cursor: pointer;
        }

        #header-search {
            width: 715px;
            position: relative;

            #glass {
                position: absolute;
                color: rgba(255, 255, 255, 0.513);
                cursor: pointer;
                top: 30%;
                left: 4%;
                padding: 4px;
                z-index: 1;
                font-size: 15px;

                &:hover {
                    color: white;
                }
            }

            .fa-camera {
                position: absolute;
                color: rgba(255, 255, 255, 0.513);
                cursor: pointer;
                top: 30%;
                right: 2%;
                padding: 4px;
                z-index: 1;
                font-size: 18px;

                &:hover {
                    color: white;
                }
            }

            .search {
                width: 100%;
                height: 58.5px;
                border-radius: 56px;
                border: none;
                background-color: rgba(255, 255, 255, 0.177);
                outline: none;
                color: white;
                padding: 0px 50px;
                font-size: 18px;
                font-weight: 700;
                cursor: pointer;
                margin: 0px 10px;
                position: relative;
                z-index: 3;
            }

            .search:hover {
                background-color: rgba(255, 255, 255, 0.333);
            }

            .search-active {
                color: black;
                background-color: white;
                &:hover {
                    background-color: #fff;
                }
            }

            ::placeholder {
                color: rgba(255, 255, 255, 0.398);
                font-size: 18px;
                font-weight: 700;
            }

            .show {
                display: none;
                position: absolute;
                left: 10px;
                top: 30px;
                width: 715px;
                z-index: 2;
                background-color: #fff;
                border-radius: 0px 0px 20px 20px;

                & li:first-child {
                    margin-top: 35px;
                }

                & li:last-child {
                    margin-bottom: 10px;
                }

                & li {
                    padding: 10px 0px;
                    margin: 0px 10px;
                    border-radius: 10px;
                    cursor: pointer;

                    &:hover {
                        background-color: rgba(128, 128, 128, 0.101);
                    }

                    & a {
                        color: black;
                    }
                }

                .fa-magnifying-glass {
                    color: gray;
                    margin: 0px 15px;
                    font-size: 15px;
                }
            }

            #search-photo {
                position: absolute;
                background-color: white;
                z-index: 2;
                padding: 10px;
                font-size: 16px;
                border-radius: 10px;
                box-shadow: 0px 0px 6px 2px rgba(128, 128, 128, 0.413);
                top: 100%;
                right: -7%;
                display: none;
            }

        }

        #location,
        #user,
        #basket {
            display: flex;
            flex-direction: column;
            align-items: center;
            background-color: transparent;
            border: none;
            cursor: pointer;
            color: rgba(255, 255, 255, 0.452);
            outline: none;
            font-size: 16px;
            margin: 0px 10px;

            #header-basket-sum {
                display: flex;
                justify-content: center;
                align-items: center;
                position: absolute;
                width: 20px;
                height: 20px;
                background-color: rgb(255, 64, 0);
                border-radius: 50%;
                font-size: 14px;
                top: -7px;
                right: 25px;
                // display: none;
            }
        }

        #location:hover,
        #user:hover,
        #basket:hover {
            color: white;
        }

        .fa-location-dot,
        .fa-user,
        .fa-cart-shopping {
            background-color: rgba(255, 255, 255, 0);
            font-size: 20px;
            color: white;
            margin-bottom: 10px;

        }
    }
